import { useState } from 'react';
import './LunchChooser.component.css';

let lunchSelection;

function lunchRoller() {
    let lunchOptions = ['Padmini\'s', 'H-Mart', 'Ledo\'s', 'The Garage', 'Shawn\'s House'];

    // lunchSelection = Math.floor(Math.random() * lunchOptions.length);
    let randomNum = Math.floor(Math.random() * lunchOptions.length);

    // lunchSelection = lunchOptions[randomNum];
    return lunchSelection = 'Today you shall eat at ' + lunchOptions[randomNum] + '.';

    console.log(lunchSelection);

    // console.log('Today you shall eat at ' + lunchOptions[randomNum]);
}

export default function LunchChooser() {
    let newLunchSelection = useState(lunchSelection);

    return (
        <div className="choiceBody">
            <h2>Where shall you go for lunch today?</h2>
            <button className="choiceButton" onClick={lunchRoller}>-THE GODS WILL DECIDE-</button>
            <p>{newLunchSelection}</p>
        </div>
    );
}
